package com.mycompany.study.ch02;

public class ExInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int var1 = 10; // 십진수
		int var2 = 012; // 8진수
		int var3 = 0xa; // 16진수
		
		System.out.println("var 1 : " + var1);
		System.out.println("var 2 : " + var2);
		System.out.println("var 3 : " + var3);
		
	}
	

}
